import { useState, useEffect } from "react";

const useMovieListItem = (movie) => {
  const getColor = () => {
    if (movie.vote_average >= 8) {
      setRating("green");
    } else if (movie.vote_average >= 5) {
      setRating("yellow");
    } else {
      setRating("red");
    }
  };

  const [rating, setRating] = useState(getColor());

  return { rating };
};

export default useMovieListItem;
