import { useState, useEffect } from "react";

const useMovieListItem = (movie) => {
  const [rating, setRating] = useState();

  if (movie.vote_average >= 8) {
    setRating("green");
  } else if (movie.vote_average >= 5) {
    setRating("yellow");
  } else {
    setRating("red");
  }

  return { rating };
};

export default useMovieListItem;
